import { useState } from 'react';
import * as C from './styles';
import { Item } from '../../types/item';
type Props = {
  item: Item,
  handleChecked: (taskChecked: boolean, taskId: number) => void
}

export const ListItem = ({ item, handleChecked }: Props) => {
  const [isChecked, setIsChecked] = useState(item.done);

  const handleClickCheck = (id: number, checked: boolean) => {
    handleChecked(checked, id);
  }

  return (
    <C.Container done={isChecked} >
      <input 
        type="checkbox"
        checked={ item.done }
        onChange={ e => handleClickCheck(item.id, e.target.checked) }
      />
      <label>
        { item.name }
      </label>
    </C.Container>
  );
}
