import styled from "styled-components";

export const Container = styled.div`
  padding: 10px;
  margin: 20px 0;
  display: flex;
  align-items: center;
  justify-content: center;

  .action__buttom {
    background: transparent;
    border: 1px solid #555;
    border-radius: 15px;
    color: #eee;
    font-size: 18px;
    padding: 5px 10px;
    margin: 0 5px;
  }
`;
